mapper.select:
SELECT COUNT(*)
FROM TEST_PATIENT
WHERE PT_NO = #{patientNo};

mapper.update:
UPDATE TEST_PATIENT
SET STATUS = #{status}, LAST_MODIFIED_BY = #{lastModifiedBy}, LAST_MODIFIED_DATE = #{lastModifiedDate}


        CREATED_BY = #{createdBy},


        CREATED_DATE=#{createdDate},


WHERE PT_NO = #{ptNo};
